import React, { Component } from 'react'

export default class Playground extends Component {
    render() {
        let tempComp = '<div id="meta"> <textarea name="metaKeywords" id="metaKeywords" placeholder="Separate keywords or phrases with a comma (example: html code generator, generate html, ...)"></textarea></div>'
        let tempStyle = {
            backgroundColor: 'blue',
            padding: '20px',
            color: 'red'
        }
        let markup = {
            __html: tempComp
        }
        return ([
           
            <h1 key='h1' className="heading">This is profile card!</h1>,
          
            <Sample key='h2' />,
           
            <div key="div1" style={tempStyle} dangerouslySetInnerHTML={markup}></div>,
            <FunctionalComp key="func1" no={1} flag="true" />, 
           
            [1, 2, 3, 4].map(function (item, index) {
                return <FunctionalComp key={'item' + index} no={item} name="priyanka" />
            })
        ])
    }
}

class Sample extends Component {
    render() {
        return <h2>Sample card</h2>
    }
}

function FunctionalComp(props) {
    let text = 'This is a good card'
    //let fullText = text+' '+ props.no
    let fullText = `${text} ${props.no}`
    return (
        <div>
            <h3>This is a profile card {props.no}</h3>
            <h4>{fullText}</h4>
        </div>
    )
}
